import React from 'react'
import Sidepanel from './Sidepanel/Sidepanel';
import WebSocketInstance from '../websocket';

class Chat extends React.Component {

    constructor(props){
        super(props);
        this.state={};
        this.waitForSocketConnection(()=>{
            WebSocketInstance.addCallbacks(this.setMessages.bind(this), this.addMessage.bind(this));
            console.log('PROPS');
            console.log(this.props);
            console.log(this.props.currentUser);
            console.log('------------------------');
            WebSocketInstance.fetchMessages(this.props.currentUser)
        });
    }
    waitForSocketConnection(callback){
        const component = this;
        setTimeout(
            function(){
                if (WebSocketInstance.state()===1){
                    console.log('connection is secure');
                    callback();
                    return;
                } else {
                    console.log('waiting for connection....');
                    component.waitForSocketConnection(callback)
                }
            }, 100)
    }

    addMessage(message){
        this.setState({messages: [...this.state.messages, message]});
    }

    setMessages(messages){
        console.log('------ARRATATATAT-----------');
        console.log(messages);
        this.setState({messages: messages.reverse()})
    }

    messageChangeHandler = e =>  {
        this.setState({
            message: e.target.value
        })
    }

    sendMessageHandler = e => {
        e.preventDefault();
        const messageObject = {
            from: "jcambron",
            content: this.state.message,
        };
        WebSocketInstance.newChatMessage(messageObject);
        this.setState({
            message: ''
        });
    }

    renderMessages = (messages)=>{
        const currentUser = 'jcambron';
        console.log('MESSAGES');
        console.log(messages);
        console.log('----------------------');
        return messages.map(message => (
            <li key={message.id}  className={message.author === currentUser ?  'sent':'replies'}>
                <img src="http://emilcarlsson.se/assets/mikeross.png"/>
                <p>{message.content}
                    <br />
                    <small className={message.author === currentUser ? 'sent' : 'replies'}>
                    {Math.round((new Date().getTime() - new Date(message.timestamp).getTime())/60000)} minutes ago
                    </small>
                </p>
            </li>
        ))
    }


    render() {
        const messages = this.state.messages;
        return (
            <div id="frame">
                <Sidepanel/>
                <div className="content">
                    <div className="contact-profile">
                        <img src="http://emilcarlsson.se/assets/harveyspecter.png" alt="" />
                        <p>Harvey Specter</p>
                        <div className="social-media">
                            <i className="fa fa-facebook" aria-hidden="true"></i>
                            <i className="fa fa-twitter" aria-hidden="true"></i>
                            <i className="fa fa-instagram" aria-hidden="true"></i>
                        </div>
                    </div>
                    <div className="messages">
                        <ul id="chat-log">
                            {
                                messages && 
                                this.renderMessages(messages)
                            }
                        </ul>
                    </div>
                    <div className="message-input">
                        <form onSubmit={this.sendMessageHandler}>
                            <div className="wrap">
                                <input onChange={this.messageChangeHandler} value={this.state.message} id="chat-message-input" type="text" placeholder="Write your message..." />
                                <i className="fa fa-paperclip attachment" aria-hidden="true"></i>
                                <button id="chat-message-submit" className="submit">
                                    <i className="fa fa-paper-plane" aria-hidden="true"></i>
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default Chat;
